mapper.select:
SELECT
	*
FROM
(
	SELECT
		rownum rn,

	FROM

		LEFT OUTER JOIN
			prod_ins pi
		ON
			oi.pi_no = pi.pi_no
		LEFT OUTER JOIN
			prod p
		ON
			p.pd_no = pi.pd_no


			TRUNC(o.od_date) >= #{dateBegin}


			AND TRUNC(o.od_date) <= #{dateEnd}




					o.od_no = #{keyword}


					p.pd_name LIKE ('%'||#{keyword}||'%')


					pi.pd_no = #{keyword}


					m.mem_name = #{keyword}


					o.mem_no = #{keyword}



		AND rownum <= #{pageNum} * #{amount}

	GROUP BY
		rownum, o.od_no, o.mem_no, o.od_date, o.od_prog, mem_name
	ORDER BY
		o.od_no DESC
) orderlist
WHERE
	rn > (#{pageNum} - 1) * #{amount}

SELECT
	COUNT(*)
FROM

	LEFT OUTER JOIN
		prod_ins pi
	ON
		oi.pi_no = pi.pi_no
	LEFT OUTER JOIN
		prod p
	ON
		p.pd_no = pi.pd_no


		TRUNC(o.od_date) >= #{dateBegin}


		AND TRUNC(o.od_date) <= #{dateEnd}




				o.od_no = #{keyword}


				p.pd_name LIKE ('%'||#{keyword}||'%')


				pi.pd_no = #{keyword}


				m.mem_name = #{keyword}


				o.mem_no = #{keyword}




GROUP BY
	rownum, o.od_no, o.mem_no, o.od_date, o.od_prog, mem_name
